import React, {Component} from 'react';
import Modal from 'react-modal';
import Validation from 'react-validation';
import Form from 'react-validation/build/form';
import Input from 'react-validation/build/input';
import Button from 'react-validation/build/button';
import '../validation.js';

export default class Icons extends Component {
    constructor(props) {
        super(props)
        this.state = {
          data: [],
          modalIsOpen: false,
          name: '',
          email: '',
          msg: '',
          id: 0
        }

        this.openModal = this.openModal.bind(this);
        this.closeModal = this.closeModal.bind(this);
        this.logChange = this.logChange.bind(this); // We capture the value and change state as user changes the value here.
        this.handleEdit = this.handleEdit.bind(this); // Function where we submit data
    }

    openModal(member) {
        this.setState({
            modalIsOpen: true,
            name: member.name,
            email: member.email,
            id: member.id
        }, function() {
            console.log(this.state.value);
        });
    }

    closeModal() {
        this.setState({
            modalIsOpen: false
        });
    }

    logChange(e) {
        this.setState({
            [e.target.name]: e.target.value //setting value edited by the admin in state.
        });
    }

    handleEdit(event) {
        //Edit functionality
        event.preventDefault()
        var data = {
            name: this.state.name,
            email: this.state.email,
            id: this.state.id
        }
        fetch("http://localhost:9000/users/edit", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then(function(response) {
            if (response.status >= 400) {
                throw new Error("Bad response from server");
            }
            return response.json();
        }).then(function(data) {
            console.log(data)
            if (data === "success") {
                this.setState({
                    msg: "User has been edited."
                });
            }
        }).catch(function(err) {
            console.log(err)
        });
    }

    deleteMember(member){
        var data = {
            id: member.id
        }
        fetch("/users/delete", {
            method: 'POST',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify(data)
        }).then(function(response) {
            if (response.status >= 400) {
              throw new Error("Bad response from server");
            }
            return response.json();
        }).then(function(data) {
            if(data === "success"){
               this.setState({msg: "User has been deleted."});  
            }
        }).catch(function(err) {
            console.log(err)
        });
    }
    
    componentDidMount() {
      fetch('http://localhost:9000/users')
        .then(response => response.json())
        .then(data => this.setState({ data }));
    }

    componentDidUpdate(prevProps, prevState) {
        if (this.state.value > prevState.value) {
          this.foo();  
        }
      }

    render() {
        return (
        <div className="container"> 
            <div className="panel panel-default p50 uth-panel">
                <table className="table table-hover">
                    <thead>
                        <tr>
                            <th>Member name</th>
                            <th>Member email</th>
                            <th>Blood Group</th>
                            <th>Phone number</th>
                            <th>Dob</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                    {this.state.data.map(member =>
                        <tr key={member.id}>
                        <td>{member.name} </td>
                        <td>{member.email}</td>
                        <td>{member.bloodGroup}</td>
                        <td>{member.phone_number}</td>
                        <td>{member.dob}</td>
                        <td><a onClick={() => this.openModal(member)}>Edit</a>|
                            <a onClick={() => this.deleteMember(member)}>Delete</a></td>
                        </tr>
                    )}
                        <Modal
                            isOpen={this.state.modalIsOpen}
                            onRequestClose={this.closeModal}
                            contentLabel="Example Modal" >
                        <Form onSubmit={this.handleEdit} method="POST">
                            <label>Name</label>
                            <Input onChange={this.logChange} className="form-control" value={this.state.name} placeholder='John' name='name' validations={['required']}/>
                            <label>Email</label>
                            <Input onChange={this.logChange} className="form-control" value={this.state.email} placeholder='email@email.com' name='email' validations={['required', 'email']}/>
                            <div className="submit-section">
                            <Button className="btn btn-uth-submit">Submit</Button>
                            </div>
                        </Form>
                        </Modal>
                    </tbody>
                </table>
            </div>
        </div>
        );
    }
}
